// App.js

import React, { useState } from "react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import Product from "./Product";
import { productData, responsive } from "./data";
import FormPage from "./FormPage";

function App() {
  const [selectedBook, setSelectedBook] = useState(null);

  const handleBuyNow = (name, price) => {
    setSelectedBook({ name, price });
  };

  const product = productData.map((item) => (
    <Product
      key={item.id} // Add a unique key for each product item
      name={item.name}
      url={item.imageurl}
      desc={item.desc}
      price={item.price}
      onBuyNow={handleBuyNow}
    />
  ));

  return (
    <div className="App">
      <h1>E-Book Online Store</h1>
      <Carousel responsive={responsive}>{product}</Carousel>
      {selectedBook && <FormPage selectedBook={selectedBook} />} {/* Pass the selectedBook prop */}
    </div>
  );
}

export default App;
