import React from "react";

export default function Product (props) {
    var bookName = props.name;
    var bookPrice = props.price;

    const handleBuyNow = () => {
        // Call the callback function with the book information
        props.onBuyNow(bookName, bookPrice);
      };

    return (
        <div className='card'>
          <img src={props.url} alt='Product Image' className="product--image"></img>
          <h2>{props.name}</h2>
          <p className='price'>$ {props.price}</p>
          <p className="description"> {props.desc}</p>
          <p>
            <button src="./form" onClick={handleBuyNow}>Buy now</button>
          </p>

        </div>
    )
}
