import React, { useState, useEffect } from "react";
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";

export default function Product(props) {
  const [showDownloadLink, setShowDownloadLink] = useState(false);

  useEffect(() => {
    if (showDownloadLink) {
      // Code to trigger the download here
      // You can create a link or a button and trigger a click event programmatically
      // For example:
      const downloadLink = document.createElement("a");
      downloadLink.href = `/path/to/public/folder/${props.name}.ebook`;
      downloadLink.download = `${props.name}.ebook`;
      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
    }
  }, [showDownloadLink, props.name]);

  return (
    <div className="card">
      <img src={props.url} alt="Product Image" className="product--image" />
      <h2>{props.name}</h2>
      <p className="price">$ {props.price}</p>
      <p className="description"> {props.desc}</p>
      <p>
        <PayPalScriptProvider
          options={{ "client-id": "ASd9mVmhzF-GQt82tr7NatGMO1-hSfNApQD9ocZZvnGAZufZZ9E3FlOsanBH6uiFPnWyzT-j2Q-IJMeX" }}
        >
          <PayPalButtons
            createOrder={(data, actions) => {
              return actions.order.create({
                purchase_units: [
                  {
                    amount: {
                      value: props.price, // Removed quotation marks here
                    },
                  },
                ],
              });
            }}
            onApprove={(data, actions) => {
              return actions.order.capture().then((details) => {
                const name = details.payer.name.given_name;
                alert(`Transaction completed by ${name}`);
                setShowDownloadLink(true); // Set the state to true to show the download link
              });
            }}
          />
        </PayPalScriptProvider>
      </p>
      {showDownloadLink && (
        <p>
          <a href={`/path/to/public/folder/${props.name}.ebook`} download={`${props.name}.ebook`}>
            Download Your File
          </a>
        </p>
      )}
    </div>
  );
}
